const crypto = require("crypto");
const nodemailer = require("nodemailer");
import { getConnection } from  "../database/database";
import cryptos, { hash } from "crypto";

const checkEmailExists = async (req, res) => {
    try {
        const { email } = req.body;
        const connection = await getConnection();
        const result = await connection.query("SELECT id FROM users WHERE email = ?", [email]);

        if (result.length > 0) {
            res.json({ exists: true });
        } else {
            res.json({ exists: false });
        }
    } catch (error) {
        res.status(500).send(error.message);
    }
};


const sendRecoveryEmailCode = async (req, res) => {
    const { email } = req.body;
    if (!email) return res.status(400).json({ message: "Email is required" });

    // Generar un cdigo de 4 dgitos
    const code = Math.floor(1000 + Math.random() * 9000).toString();

    // Guardar el cdigo en una variable temporal (puedes usar un almacenamiento ms persistente)
    req.session.recoveryCode = code;

    // Configuracin del transportador de correo (puede requerir ajustes segn tu proveedor de correo)
    const transporter = nodemailer.createTransport({
        service: 'Gmail', // Cambia esto si usas otro servicio
        auth: {
            user: process.env.EMAIL_USER,
            pass: process.env.EMAIL_PASS
        }
    });

    // Configuracin del mensaje
    const mailOptions = {
        from: process.env.EMAIL_USER,
        to: email,
        subject: 'Recuperacin de Contrasea',
        text: `Su cdigo para recuperar su contrasea es: ${code}`
    };

    try {
        // Enviar el correo
        await transporter.sendMail(mailOptions);
        res.json({ message: "Cdigo enviado al correo.", code });
    } catch (error) {
        console.error("Error al enviar el correo:", error);
        res.status(500).json({ message: "Error al enviar el correo." });
    }
};

